import React, {useState} from "react";
import { Link } from 'react-router-dom';
import axios from 'axios';


export default function AuthenticationForm() {
        const [name, setName] = useState('');
        const [password, setPassword] = useState('');

        const handleSubmit = async (e) => {
            e.preventDefault();
            const response = await fetch('http://127.0.0.1:8000/login_view/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ name, password }),
            });
            if (response.ok) {
              window.location.href = 'http://localhost:3000/trainingCompass/'
            } else {
            }
        };

        return (
            <form className="form" onSubmit={handleSubmit}>
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Имя" required />
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Пароль" required />
                <button type="submit" className="button">Вход</button>
            </form>
        );
}
