const initialState = {
  items: [],
};

const indexTask = (state = initialState, action) => {
  if (action.type === "ACTIONS_INDEX_TASK") {
    return {
      ...state,
      items: [state.items, action.payload],
    };
  }
  return state;
};

export default indexTask;
