const initialState = {
  items: [],
};

const indexTask = (state = initialState, action) => {
  if (action.type === "ACTIONS_INDEX_TASK") {
    const stateItems = "";
    if (action.payload === []) {
      stateItems = action.payload;
    } else {
      stateItems = state.items += action.payload + ",";
    }
    // const stateItems = (state.items += action.payload);
    // console.log(stateItems);
    return {
      ...state,
      items: stateItems,
    };
  }
  return state;
};

export default indexTask;
