import React, {useState} from "react"
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function AuthenticationForm() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      const res = await axios.get("http://127.0.0.1:8000/login/", { name, password })
      if (res.status !== 200){
        alert('не верный логин или пароль')
      }
      else {
        localStorage.setItem('name', res.data.name)
        localStorage.setItem('key', res.data.key)
      }
    }catch (err) {
      console.log(err)
    }
  }

  return (
    // <div className="block-registration">
        <form onSubmit={handleSubmit} className="form">
          <input type="name" id="name" value={name} onChange={(event) => setName(event.target.value)} className="text-style" placeholder={"Имя"} required/>
          <input type="password" id="password" value={password} onChange={(event) => setPassword(event.target.value)} className="text-style" placeholder={"Пороль"} required/>
          <div className="block-button">
            <button type="submit" className="button">
            <button type="submit" className="btn-registration">Войти</button>
              {/* <Link to={"trainingCompass"}>
                Вход
              </Link> */}
            </button>
          </div>
        </form>
      // </div>
  )
}


