import React from 'react'
import { useState, useEffect } from 'react';
import { useSelector, useDispatch} from "react-redux";
import axios from 'axios';
import { actionIndexTask } from '../../redux/actions/actionIndexTask';

export default function Task() {

    const dispatch = useDispatch()

    const tasks = useSelector(state => state.questionsTasksReducer.items)
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const handleNextQuestion = () => {
        setCurrentQuestionIndex(prevIndex => prevIndex + 1);
      };
    const handleShowAnswer = () => {
        const currentQuestion = tasks[currentQuestionIndex];
        alert(currentQuestion.answer);
      };
    const itemsCategories = useSelector(state => state.selectedCategory.items)
    useEffect(() => {
        if (currentQuestionIndex === tasks.length) {
          axios.post("http://127.0.0.1:8000/categories/", {category:itemsCategories})
            .then(() => {
              setCurrentQuestionIndex(0);
            })
            .catch(error => {
              console.log(error);
            });
        }
      },
      [currentQuestionIndex], 
      dispatch(actionIndexTask(tasks[currentQuestionIndex].id))
      );


  return (
    <div className="block-program">
      <div className="block-category">
        <div className="block-question">
            <div className="question-name">{tasks[currentQuestionIndex]?.question}</div>
          <div className="response-name">
            <div className="answer-button" onClick={handleShowAnswer}>ВОПРОС</div>
            <div className="answer-button" onClick={handleNextQuestion}>NEXT</div>
          </div>
        </div>
      </div>
    </div>
  )
}
