const initialState = {
  items: [],
};

const categoriesReducer = (state = initialState, action) => {
  if (action.type === "ACTIONS_CATEGORIES") {
    return {
      ...state,
      items: action.payload,
    };
  }
  return state;
};

export default categoriesReducer;
