import React from 'react'
import axios from 'axios';
import { useSelector, useDispatch } from "react-redux";
import { fetchModels } from "../../redux/actions/actionsCategories";
import questionsTasksReducer from '../../redux/actions/actionsQuestionsTasks'



export default function TrainingCompass(props) {

  const dispatch = useDispatch();

  React.useEffect(() => {
    dispatch(fetchModels());
  }, [dispatch]);

  const items = useSelector(state => state.categoriesReducer.items);

  const onSelectItems = (category)=>{
    axios
    .post("http://127.0.0.1:8000/categories/", {category:category})
    .then((response) => {
      console.log(response);
      dispatch(questionsTasksReducer(response.data))
    })
    .catch((error) => {
      console.log(error);
    });
};




  return (
    <div className="content">
    <div className="category-block">
      <div className="block-category">
        <div className="category-text">          
        {items.map((el)=> (<div onClick={()=>{onSelectItems(el.category)}} className="name-category" key={el.id}>{el.title}</div>))}
        </div>
      </div>
    </div>
    <div className="block-program">
      <div className="block-category">
        <div className="block-question">
          <div className="question-name">question-name</div>
          <div className="response-name">
            <div className="answer-button">ВОПРОС</div>
            <div className="answer-button">NEXT</div>
          </div>
        </div>
      </div>
    </div>
  </div>
  )
}
